<? /* Template Name: Cart */ ?><? get_header(); ?>    <main class="content">        <div class="container">            <h1 class="title main-title line"><? the_title() ?></h1>            <? the_post_content() ?>            <? if (WC()->cart->get_cart_contents_count() !== 0): ?>                <?                $saved = nails()->woo->queries()->wishlist();                if ($saved) :?>                    <h2 class="title secondary-title line">Saved for Later</h2>                    <div class="row catalog base-indent wishlist-list">                        <?                        global $saved;                        while ($saved->have_posts()) {                            $saved->the_post();                            wc_get_template_part('content', 'product');                        }                        wp_reset_query();                        $saved = false;                        ?>                    </div>                <? endif; ?>                <section class="section-recently base-indent">                    <h2 class="title secondary-title line">Recently Viewed Products</h2>                    <div class="owl-carousel owl-theme carousel-top base-indent">                        <?                        $viewed = nails()->woo->queries()->viewed(8);                        while ($viewed->have_posts()) {                            $viewed->the_post();                            wc_get_template_part('content', 'product');                        }                        wp_reset_query();                        ?>                    </div>                </section>            <? endif; ?>        </div>    </main><? get_footer() ?>