<?$children = get_terms('product_cat', [    'child_of' => get_queried_object_id()]);if (!is_tax()) $children = nails()->woo->parentCategories();$isEquipment = !empty($children);?><? get_header(); ?>    <main class="content">        <div class="container">            <h1 class="title main-title line"><? woocommerce_page_title() ?></h1>            <div class="notices-area w-100"><? wc_print_notices() ?></div>            <div class="row base-indent <?= $isEquipment?'equipment':'catalog' ?>">                <?                if ($isEquipment && !is_search()) {                    global $category;                    foreach ($children as $category)                        wc_get_template_part('content', 'product_cat');                } else if (wc_get_loop_prop('total')) {                    while (have_posts()) {                        the_post();                        wc_get_template_part('content', 'product');                    }                } else { ?>                    <div class="col-12">                        <? do_action('woocommerce_no_products_found'); ?>                    </div>                    <?                } ?>            </div>            <? if (!$isEquipment) woocommerce_pagination() ?>        </div>    </main><? get_footer();