<?phpglobal $product, $wl_product, $saved;if (empty($product) || !$product->is_visible()) {    return;}$isLoop = in_the_loop();echo $isLoop || (is_wishlist() && !is_user_logged_in()) || $saved ? '<div class="col-md-4 col-lg-3 col-sm-6">' : '';echo is_wishlist() && is_user_logged_in() ? '<div class="col-lg-4 col-sm-6 wishlist_item">' : '';?>    <div <? wc_product_class('product-card'); ?>>        <? woocommerce_show_product_sale_flash() ?>        <? wc_get_template_part('single-product/stock') ?>        <a href="<? the_permalink() ?>" aria-label="product-image" class="product-card-img">            <? the_post_thumbnail('product', ['class' => 'img-fluid owl-lazy']) ?>        </a>        <? if (has_excerpt()): ?>            <div class="product-card-name"><? the_excerpt() ?></div>        <? endif; ?>        <div>            <? woocommerce_template_single_price() ?>            <? if (is_wishlist()): ?>            <div class="position-relative">                <? endif; ?>                <a rel="nofollow" href="<?= $product->add_to_cart_url() ?>" data-id="<? the_ID() ?>" data-quantity="1"                   data-product_id="<? the_ID() ?>" data-product_sku="<? the_sku() ?>"                   class="button btn-cyan add-to-cart add_to_cart_button ajax_add_to_cart"><?= esc_html($product->single_add_to_cart_text()); ?></a>                <? if (is_wishlist()): ?>                <button type="submit" class="icon delete-wishlist" name="tinvwl-remove"                        value="<?= esc_attr($wl_product['ID']); ?>"                        title="<? _e('Remove', 'ti-woocommerce-wishlist') ?>">                    <img src="<?= path() ?>assets/img/icons/delete_b.svg" alt="remove">                </button>            </div>        <? endif; ?>        </div>    </div><?= $isLoop || is_wishlist() || $saved ? '</div>' : '' ?>